    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed

         remoteserver = new jrdesktop.server.rmi.Server();
         
         
         // set ip address from jTextField1
         if(jrdesktop.Config.server_address.equals("127.0.0.1"))
         {
             String remoteip = jTextField1.getText();
             
             jrdesktop.Config.server_address = remoteip;
         }
         else
         {
             jrdesktop.Config.server_address = "127.0.0.1";
         }
         
         
         // set port from jTextField2
         if(jrdesktop.Config.server_port == 3389)
         {
             String remoteport = jTextField2.getText();
             jrdesktop.Config.server_port =   Integer.valueOf(remoteport);
             
         }
         
         
         remoteserver.Start();
         
      

         updateStatus();
    }//GEN-LAST:event_jButton1ActionPerformed


      public void updateStatus() {
       
          if(remoteserver.isRunning())
          {
              jTextAreaStatus.setText( "Server is running" + "\n" + Server.getStatus());
          }
          else if(remoteserver.isDaemon())
          {
              jTextAreaStatus.setText("Server Daemon is started" + "\n");
          }
          
    }
    
